import { ChildResult, Result } from "../Result.mjs";

//#region src/validators/validateBundleDependencies.ts
/**
* Validate the `bundleDependencies` field in a package.json, which can either be
* an array of strings or a boolean.
*
* ["renderized", "super-streams"]
*/
const validateBundleDependencies = (obj) => {
	const result = new Result();
	if (typeof obj === "boolean") return result;
	else if (Array.isArray(obj)) for (let i = 0; i < obj.length; i++) {
		const childResult = new ChildResult(i);
		const item = obj[i];
		if (typeof item !== "string") {
			const itemType = item === null ? "null" : typeof item;
			childResult.addIssue(`item at index ${i} should be a string, not \`${itemType}\``);
		} else if (item.trim() === "") childResult.addIssue(`item at index ${i} is empty, but should be a dependency name`);
		result.addChildResult(childResult);
	}
	else if (obj == null) result.addIssue("the value is `null`, but should be an `Array` or a `boolean`");
	else {
		const valueType = typeof obj;
		result.addIssue(`the type should be \`Array\` or \`boolean\`, not \`${valueType}\``);
	}
	return result;
};

//#endregion
export { validateBundleDependencies };